# 5)
# how many elements are in each of the following lists?
first_list = ['drs', 'clt', 'iny', 'alv', 'nvy', 'bpt', 'gkw', 'fkm', 'jrz', 'hov', 'bqu', 'bov', 'eju', 'eiz', 'fjm', 'bek', 'abj', 'hov', 'coq', 'iox', 'efs', 'krw', 'evy', 'elw', 'gil', 'ajq', 'cek', 'fkm', 'mnu', 'adf']
second_list = ['dlz', 'akw', 'bry', 'eyz', 'bny', 'kst', 'elw', 'ekl', 'djm', 'aft', 'gkw', 'krw', 'coq', 'evy', 'bov', 'bkl', 'bov', 'afs', 'hov', 'fjq', 'cqu', 'ahq', 'beh', 'ijz', 'ksy', 'ilx', 'htu', 'epz', 'ekl', 'ajq']

print(f'Aufgabe 5:\nDie Anzahl an Elementen in first_list betraegt: {len(first_list)}\nDie Anzahl an Elemente in second_list betraegt: {len(second_list)}\n')

# 6)
# which and how many elements are common to the two lists above? 
common_elements = set(first_list).intersection(second_list)
print(f'Aufgabe 6:\nGemeinsame Elemente: {common_elements}\nAnzahl der gemeinsamen Elemente betraegt: {len(common_elements)}\n')



# 7)
# how often does the character 'a' occur in each of the lists above?

def count_letter_in_list(letter, list):
    count = 0
    for elmnt in list: 
        count += elmnt.count(letter)
    return count

amount_in_first = count_letter_in_list('a', first_list)
amount_in_second = count_letter_in_list('a', second_list)

print(f'Aufgabe 7:\nDer Buchstabe "a" kommt in first_list {amount_in_first} mal vor.\nDer Buchstabe "a" kommt in second_list {amount_in_second} mal vor.\n')

