# 6)
# which and how many elements are common to the two lists above? 
print(f"Die Elemente: {set(first_list) & set(second_list)} sind in beiden Listen \nund es sind {len(set(first_list) & set(second_list))} Elemente")
print(" ")
# 7)
# how often does the character 'a' occur in each of the lists above?
count1 = 0
for i in range(len(first_list)):
    element = first_list[i]
    for j in element:
        if j == "a":
            count1 = count1 + 1
print(f"In 'first_list' ist 'a' {count1}-mal")

count2 = 0
for m in range(len(second_list)):
    element = second_list[m]
    for n in element:
        if n == "a":
            count2 = count2 + 1
print(f"In 'second_list' ist 'a' {count2}-mal")
print(" ")

