# 5)
# how may elements are in each of the following lists?
first_list = ['drs', 'clt', 'iny', 'alv', 'nvy', 'bpt', 'gkw', 'fkm', 'jrz', 'hov', 'bqu', 'bov', 'eju', 'eiz', 'fjm', 'bek', 'abj', 'hov', 'coq', 'iox', 'efs', 'krw', 'evy', 'elw', 'gil', 'ajq', 'cek', 'fkm', 'mnu', 'adf']
second_list = ['dlz', 'akw', 'bry', 'eyz', 'bny', 'kst', 'elw', 'ekl', 'djm', 'aft', 'gkw', 'krw', 'coq', 'evy', 'bov', 'bkl', 'bov', 'afs', 'hov', 'fjq', 'cqu', 'ahq', 'beh', 'ijz', 'ksy', 'ilx', 'htu', 'epz', 'ekl', 'ajq']

count_first_list = len(first_list)
count_second_list= len(second_list)

print("there are", count_first_list, "elements in the first list and", count_second_list, "elements in the second list")

# 6)
# which and how many elements are common to the two lists above? 

first_set = set(first_list)
second_set = set(second_list)
common_elements = first_set.intersection(second_set)
count_common_elements = len(common_elements)

print("the common elements in both lists are:", common_elements, "and there are", count_common_elements, "of them")

# 7)
# how often does the character 'a' occur in each of the lists above?

count_a_first_list = 0
count_a_second_list = 0

for item in first_list:

    count_a_first_list += item.count('a')

for item in second_list:
    
    count_a_second_list += item.count('a')

print("the character 'a' occurs", count_a_first_list, "times in the first list and", count_a_second_list, "times in the second list")

